import React, { useEffect, useState, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage, createChatRoom, fetchChatRooms, fetchMessages,sendMessage } from '../features/auth/chatSlice';
import { FaSmile, FaTimes } from 'react-icons/fa'; // Emoji toggle icons
import Picker from '@emoji-mart/react'; // Emoji picker component
import data from '@emoji-mart/data';
const ChatDialog = ({ roomName, user }) => {
    console.log(`roomName: ${roomName}`);
    const dispatch = useDispatch();
    const token = useSelector((state) => state.auth.token);
    const messages = useSelector((state) => state.chat.messages);
    const [messageInput, setMessageInput] = useState('');
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);
    const socketRef = useRef(null); // Using useRef for socket
    const chatRooms = useSelector((state) => state.chat.rooms);
    const currentUser = useSelector((state) => state.user.user);
    const room = useSelector((state)=>state.chat.rooms)
    const messagesEndRef = useRef(null);
    console.log(`roomsssss${room}`)
    console.log(messages)
    console.log(user.id,currentUser.id)

    useEffect(() => {
        if (roomName && token) {

            dispatch({ type: 'chat/resetMessages' });
            // Step 1: Fetch all chat rooms to check if the room exists
            dispatch(fetchChatRooms({ token })).then((action) => {
                const roomExists = chatRooms.some(room => room.name === roomName);

                if (roomExists) {
                    // Room exists, fetch messages for this room
                    dispatch(fetchMessages({ roomName, token }));
                } else {
                    // Room doesn't exist, create it first
                    dispatch(createChatRoom({ roomName,participants:[user.id,currentUser.id], token }))
                        .then(() => {
                            // After creating the room, fetch the messages
                            dispatch(fetchMessages({ roomName, token }));
                        });
                }
            });
        }
       


        // WebSocket connection setup
        socketRef.current = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/?token=${token}`);

        socketRef.current.onopen = () => {
            console.log("WebSocket connection opened");
        };
        
        socketRef.current.onmessage = (e) => {
            const data = JSON.parse(e.data);
            console.log("WebSocket data received:", data); 
            if (data && data.message && data.sender) {
                dispatch(addMessage({
                    sender: data.sender,
                    message: data.message,
                    timestamp: data.timestamp || new Date().toISOString(),  // Use provided timestamp or current time if not available
                }));
            }
            // dispatch(addMessage({
            //     sender: data.sender,
            //     message: data.message,
            //     timestamp: new Date().toISOString(),  // Or use the timestamp from data if available
            // }));
        };

        
        socketRef.current.onclose = (event) => {
            console.log('WebSocket closed. Attempting to reconnect...');
            console.error("WebSocket closed unexpectedly:", event.reason);
        };
        

        return () => {
            socketRef.current.close();
        };
    }, [roomName, token, dispatch]);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    const handleSendMessage = () => {
        if (messageInput && socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
            socketRef.current.send(JSON.stringify({
                message: messageInput,
                user_id: user.id,
            }));
            console.log(`roomname=${roomName},message:${messageInput}`)
           // dispatch(sendMessage({ roomName, message: messageInput, token }));
            setMessageInput('');
        }
    };
    const formatTimestamp = (timestamp) => {
        const date = new Date(timestamp);
        return date.toLocaleTimeString([], { hour: 'numeric', minute: '2-digit', hour12: true });
    };
    const handleAddEmoji = (emoji) => {
      setMessageInput(prevInput => prevInput + emoji.native); // Append emoji to message input
  };
    

    return (
        <div className="flex flex-col h-full border-2 border-gray-300 rounded-lg shadow-lg ">
        {/* Header */}
        <div className="flex items-center p-4 bg-blue-500 text-white sticky top-0 z-10 mt-16">
          <img
            src={user.profile_pic || 'https://via.placeholder.com/800x500'}
            alt={user.username}
            className="w-10 h-10 rounded-full mr-3 object-cover"
          />
          <span className="text-lg font-semibold">{user.username}</span>
        </div>
      
        {/* Chat messages container */}
        <div className="flex-1 overflow-y-auto bg-gray-100 p-4">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`p-2 rounded-lg mb-2 ${
                currentUser.id !== msg.sender
                  ? 'bg-blue-500 text-white self-end'
                  : 'bg-gray-400  text-white self-end'
              }`}
              style={{
                maxWidth: '50%',
                marginLeft: currentUser.id !== msg.sender ? 'auto' : '0',
              }}
            >
              <span>{msg.message}</span>
              <div className="text-xs text-orange-50 mt-1 text-right">
                {formatTimestamp(msg.timestamp)}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
      
        {/* Message input */}
        <div className="flex items-center p-2 border-t bg-white">
               {/* Emoji Picker Button */}
               <button
                    onClick={() => setShowEmojiPicker(!showEmojiPicker)}
                    className="text-gray-500 hover:text-gray-800 mr-2"
                >
                    {showEmojiPicker ? <FaTimes size={20} /> : <FaSmile size={20} />}
                </button>

                {showEmojiPicker && (
                    <div className="absolute bottom-12 left-0 z-10">
                        <Picker data={data} onEmojiSelect={handleAddEmoji} />
                    </div>
                )}
          <input
            type="text"
            className="flex-1 border rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={messageInput}
            onChange={(e) => setMessageInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
            placeholder="Type your message..."
          />
          <button
            onClick={handleSendMessage}
            className="ml-2 bg-blue-500 text-white rounded-lg px-4 py-2 hover:bg-blue-600 transition duration-200"
          >
            Send
          </button>
        </div>
      </div>
      
    );
};

export default ChatDialog;
