// profileThunks.js
import axios from 'axios';
import { BASE_URL } from '../../config';
import { setModalIsOpen, setModalImage, setImageType, setNewComment, setReplyingToCommentId, setCommentText, setShowEmojiPicker, setFollowerCount, setFollowingCount, setBookmarkPosts, setError, setLoading, setLikedPosts, setPosts } from '../profileSlice';
import { setProfile } from './profileSlice';
export const fetchProfile = async (userId, token) => {
  return (dispatch) => {
    axios.get(`${BASE_URL}/api/users/profile/${userId}/`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => {
        dispatch(setProfile(response.data));
      })
      .catch((error) => {
        dispatch(setError(error.response?.data?.detail || 'Failed to fetch profile.'));
      });
  };
};

export const fetchPosts = async () => {
  return (dispatch) => {
    axios.get(`${BASE_URL}/api/posts/`)
      .then((response) => {
        dispatch(setPosts(response.data));
      })
      .catch((error) => {
        dispatch(setError(error.response?.data?.detail || 'Failed to fetch posts.'));
      });
  };
};

export const likePost = async (postId) => {
  return (dispatch) => {
    axios.post(`${BASE_URL}/api/likes/`, {
      post: postId,
    }, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => {
        if (response.status === 201) {
          dispatch(setLike({ ...likedPosts, [postId]: true }));
        } else if (response.status === 204) {
          dispatch(setLikedPosts({ ...likedPosts, [postId]: false }));
        }
      })
      .catch((error) => {
        console.error('Error liking/unliking post:', error);
      });
  };
};

export const submitComment = async (postId, commentText, parentId = null) => {
  return (dispatch) => {
    axios.post(`${BASE_URL}/api/comments/`, {
      post: postId,
      content: commentText,
      user: user.id,
      parent: parentId,
    }, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => {
        if (response.ok) {
          return response.data;
        }
      })
      .then((data) => {
        dispatch(setComments({ ...comments, [postId]: [...(comments[postId] || []), data] }));
        dispatch(setNewComment(''));
        dispatch(setReplyingToCommentId(null));
      })
      .catch((error) => {
        console.error('Error submitting comment:', error);
      });
  };
};
