import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { fetchRoom } from '../features/auth/groupChatSlice';

const GroupChatBox = () => {
  const { roomId } = useParams(); 
  const token = useSelector((state)=>state.auth.token)  
  const dispatch = useDispatch();
  const { group, loading, error } = useSelector((state) => state.groupChat);

  useEffect(() => {
    dispatch(fetchRoom({ roomId,token }));
  }, [dispatch, token]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }
  return (
    <div className="p-6">
         
      <h2 className="text-2xl font-bold">Chat Room: {group.group_name}</h2>
     
    </div>
  );
};

export default GroupChatBox;
