import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import { BASE_URL } from '../config';
import { FaSearch } from 'react-icons/fa'; // Import search icon from react-icons
import { useNavigate } from 'react-router-dom';

const SearchBar = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const user = useSelector((state) => state.user.user);
  const token = useSelector((state) => state.auth.token); 
  const navigate = useNavigate();

  useEffect(() => {
    if (query.length > 0) {
      searchUsers(query);
    } else {
      setResults([]);
    }
  }, [query]);

  const searchUsers = async (searchQuery) => {
    try {
      const response = await axios.get(
        `${BASE_URL}/api/interactions/search-users/`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
          params: {
            search_query: searchQuery,
          },
        }
      );
      setResults(response.data);
      setIsDropdownOpen(true);
    } catch (error) {
      console.error('Error fetching search results', error);
      setResults([]);
    }
  };

  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  const handleResultClick = () => {
    setIsDropdownOpen(false);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (query.length > 0) {
      searchUsers(query); // Call searchUsers function on submit
    }
  };

  const handleUserClick = (user) => {
    // Navigate to the user profile view and pass the user ID
    navigate(`/profileview/${user.id}`);
    setIsDropdownOpen(false); // Close dropdown after navigation
  };

  return (
    <div className="relative">
      <form onSubmit={handleSearchSubmit} className="flex items-center">
        {/* Search Input */}
        <input
          type="text"
          className="border border-gray-300 rounded-full py-2 px-4 focus:outline-none w-64 md:w-96"
          placeholder="Search users..."
          value={query}
          onChange={handleInputChange}
          onFocus={() => setIsDropdownOpen(true)}
        />
        {/* Search Icon Button */}
        <button type="submit" className="ml-2 text-gray-500">
          <FaSearch />
        </button>
      </form>

      {/* Dropdown for Results */}
      {isDropdownOpen && results.length > 0 && (
        <ul className="absolute bg-white border border-gray-300 rounded-md mt-2 w-full max-h-64 overflow-y-auto z-50 shadow-lg">
          {results.map((result) => (
            <li 
              key={result.id} 
              className="hover:bg-gray-100 cursor-pointer flex items-center py-2 px-4"
              onClick={() => handleUserClick(result)} // Call handleUserClick on click
            >
              <img
                src={result.profile_pic || 'https://via.placeholder.com/30'}
                alt={result.username}
                className="w-8 h-8 rounded-full mr-2 object-cover"
              />
              <span>{result.username}</span>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchBar;
