import React, { useState } from 'react';
import { Picker } from 'emoji-mart';
import 'emoji-mart/css/emoji-mart.css';

const BioField = ({ bio, handleProfileChange }) => {
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);

  // Function to add emoji to the bio
  const handleAddEmoji = (emoji) => {
    const newBio = bio + emoji.native;
    
    // Trigger handleProfileChange to update bio state
    handleProfileChange({
      target: {
        name: 'bio',
        value: newBio,
      },
    });
  };

  return (
    <div>
      <textarea
        name="bio"
        value={bio}
        onChange={handleProfileChange}
        placeholder="Write something about yourself..."
      />
      <button type="button" onClick={() => setShowEmojiPicker(!showEmojiPicker)}>
        {showEmojiPicker ? 'Close' : 'Add Emoji'}
      </button>
      
      {showEmojiPicker && (
        <Picker
          onSelect={handleAddEmoji} // Append emoji to bio
        />
      )}
    </div>
  );
};

export default BioField;
