import React, { forwardRef } from 'react';
import Cropper from 'react-cropper';
import 'cropperjs/dist/cropper.css';

const ImageCropper = forwardRef(({ modalImage, onSave, onClose }, ref) => {
  return (
    <div className="relative">
      <Cropper
        src={modalImage}
        style={{ height: 400, width: '100%' }}
        aspectRatio={1} // Set to 1:1 for a square, or 4/3, 16/9, etc.
        guides={false}
        ref={ref} // forward ref to parent component
        viewMode={1} // Prevents the image from being dragged outside of the crop box
        autoCropArea={1} // Ensure the crop box covers the entire image by default
        scalable={false} // Disallow scaling (zooming)
        zoomable={false} // Disallow zooming
        movable={false} // Prevent moving the crop box
        cropBoxResizable={false} 
      />
      <button
        onClick={onSave}
        className="absolute bottom-4 right-4 bg-blue-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-blue-600"
      >
        Save
      </button>
      <button
        onClick={onClose}
        className="absolute top-4 right-4 bg-gray-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-gray-600"
      >
        Close
      </button>
    </div>
  );
});

export default ImageCropper;
