import React, { useState } from 'react';

const CommentItem = ({
  comment,
  postId,
  handleSubmitComment,
  replyingToCommentId,
  setReplyingToCommentId,
  openRepliesId,
  setOpenRepliesId, // State for toggling replies
}) => {
  // Determine if this comment has replies open
  const isRepliesOpen = openRepliesId === comment.id;

  return (
    <div className="mb-4">
      <p className="text-gray-600">
        <strong>{comment.username}:</strong> {comment.content}
      </p>

      {/* Reply Button */}
      <button
        className="text-sm text-blue-500 hover:underline mr-2"
        onClick={() => setReplyingToCommentId(comment.id)}
      >
        Reply
      </button>

      {/* Close Reply Button */}
      {replyingToCommentId === comment.id && (
        <button
          className="text-sm text-red-500 hover:underline"
          onClick={() => setReplyingToCommentId(null)}
        >
          Close
        </button>
      )}

      {/* Toggle Replies for Top-Level Comments */}
      {comment.replies && comment.replies.length > 0 && (
        <button
          className="text-sm text-blue-500 hover:underline ml-4"
          onClick={() =>
            setOpenRepliesId(isRepliesOpen ? null : comment.id)
          }
        >
          {isRepliesOpen ? 'Hide Replies' : 'Show Replies'}
        </button>
      )}

      {/* Render replies recursively if replies are open */}
      {isRepliesOpen && comment.replies.length > 0 && (
        <div className="ml-4 mt-2">
          {comment.replies.map((reply) => (
            <CommentItem
              key={reply.id}
              comment={reply}
              postId={postId}
              handleSubmitComment={handleSubmitComment}
              replyingToCommentId={replyingToCommentId}
              setReplyingToCommentId={setReplyingToCommentId}
              openRepliesId={openRepliesId}
              setOpenRepliesId={setOpenRepliesId}
            />
          ))}
        </div>
      )}

      {/* Reply Form for this specific comment */}
      {replyingToCommentId === comment.id && (
        <form
          onSubmit={(e) => {
            e.preventDefault();
            const replyText = e.target.reply.value;
            if (replyText.trim()) {
              handleSubmitComment(postId, replyText, comment.id); // Pass comment.id as parentId
              e.target.reset(); // Clear the input field after submitting
              setReplyingToCommentId(null); // Close the reply form after submitting
            }
          }}
        >
          <input
            type="text"
            name="reply"
            placeholder="Write a reply..."
            className="w-full p-2 mt-2 border border-gray-300 rounded-lg focus:outline-none"
          />
          <button
            type="submit"
            className="bg-blue-500 text-white mt-2 px-4 py-2 rounded-lg"
          >
            Post Reply
          </button>
        </form>
      )}
    </div>
  );
};


export default CommentItem;
