import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import { BASE_URL } from '../config';
import { FaCamera } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import Modal from 'react-modal';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';

Modal.setAppElement('#root');

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);
  const [posts, setPosts] = useState([
    { id: 1, content: 'Just started a new project! Excited to share the progress.' },
    { id: 2, content: 'Attended a fantastic workshop today. Learned so much!' },
    { id: 3, content: 'Feeling motivated to code more today!' },
  ]);
  const [newPost, setNewPost] = useState('');
  const [coverImage, setCoverImage] = useState(null);
  const [profileImage, setProfileImage] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [modalImage, setModalImage] = useState('');
  const [crop, setCrop] = useState({ aspect: 1 });
  const [croppedImageUrl, setCroppedImageUrl] = useState('');
  const [croppingType, setCroppingType] = useState('');

  const user = useSelector((state) => state.user.user);
  const token = useSelector((state) => state.auth.token);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/profile/${user?.id}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProfile(response.data);
      } catch (err) {
        setError(err.response?.data?.detail || 'An error occurred while fetching the profile.');
      }
    };

    if (user && token) {
      fetchProfile();
    }
  }, [user, token]);

  const handleFileChange = (event, type) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        if (type === 'cover') {
          setCoverImage(reader.result);
          setCroppingType('cover');
        } else {
          setProfileImage(reader.result);
          setCroppingType('profile');
        }
        setModalIsOpen(true);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleCropChange = (newCrop) => {
    setCrop(newCrop);
  };

  const handleCropComplete = (croppedArea, pixelCrop) => {
    const canvas = document.createElement('canvas');
    const image = new Image();
    image.src = croppingType === 'cover' ? coverImage : profileImage;

    image.onload = () => {
      const ctx = canvas.getContext('2d');
      canvas.width = pixelCrop.width;
      canvas.height = pixelCrop.height;
      ctx.drawImage(
        image,
        pixelCrop.x,
        pixelCrop.y,
        pixelCrop.width,
        pixelCrop.height,
        0,
        0,
        pixelCrop.width,
        pixelCrop.height
      );

      canvas.toBlob((blob) => {
        const fileUrl = URL.createObjectURL(blob);
        setCroppedImageUrl(fileUrl);
      }, 'image/jpeg');
    };
  };

  const handleImageUpload = async (type) => {
    const formData = new FormData();
    const imageFile = new File([croppedImageUrl], `${type}-image.jpg`, { type: 'image/jpeg' });
    formData.append(type === 'cover' ? 'cover_pic' : 'profile_pic', imageFile);

    try {
      await axios.patch(`${BASE_URL}/api/users/profile/update/`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data',
        },
      });

      if (type === 'cover') {
        setCoverImage(null);
        setProfile((prevProfile) => ({
          ...prevProfile,
          cover_pic: URL.createObjectURL(imageFile),
        }));
      } else {
        setProfileImage(null);
        setProfile((prevProfile) => ({
          ...prevProfile,
          profile_pic: URL.createObjectURL(imageFile),
        }));
      }
    } catch (err) {
      setError('An error occurred while uploading the image.');
    }
  };

  const handlePostSubmit = () => {
    const newDummyPost = { id: posts.length + 1, content: newPost };
    setPosts([newDummyPost, ...posts]);
    setNewPost('');
  };

  const openModal = (imageUrl) => {
    setModalImage(imageUrl);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setModalImage('');
  };

  return (
    <div className="max-w-3xl mx-auto bg-white shadow-md rounded-md overflow-hidden mt-8">
    {/* Cover Photo */}
    <div
      className="relative h-60 bg-cover bg-center cursor-pointer"
      style={{ backgroundImage: `url(${profile?.cover_pic || 'https://via.placeholder.com/800x500'})` }}
      onClick={() => openModal(profile?.cover_pic || 'https://via.placeholder.com/800x500')}
    >
      <label htmlFor="cover-upload" className="absolute top-4 right-4 cursor-pointer bg-white p-1 rounded-full shadow-lg hover:bg-gray-100">
        <FaCamera className="text-gray-600 text-lg" />
      </label>
      <input id="cover-upload" type="file" onChange={(e) => handleFileChange(e, 'cover')} className="hidden" />
      <button
        onClick={() => handleImageUpload('cover')}
        className="absolute top-4 right-12 bg-blue-500 text-white px-2 py-1 rounded-full shadow-lg hover:bg-blue-600"
      >
        Upload
      </button>
      
      {/* Profile Picture */}
      <div className="absolute bottom-0 left-4 transform -translate-y-1/2">
        <img
          src={profile?.profile_pic || 'https://via.placeholder.com/100'}
          alt="Profile"
          className="w-24 h-24 rounded-full border-4 border-white shadow-md cursor-pointer"
          onClick={() => openModal(profile?.profile_pic || 'https://via.placeholder.com/100')}
        />
        <label htmlFor="profile-upload" className="absolute bottom-2 left-12 cursor-pointer bg-white p-1 rounded-full shadow-lg hover:bg-gray-100">
          <FaCamera className="text-gray-600 text-lg" />
        </label>
        <input id="profile-upload" type="file" onChange={(e) => handleFileChange(e, 'profile')} className="hidden" />
        <button
          onClick={() => handleImageUpload('profile')}
          className="absolute bottom-2 left-24 bg-blue-500 text-white px-2 py-1 rounded-full shadow-lg hover:bg-blue-600"
        >
          Upload
        </button>
      </div>
    </div>

    {/* Profile Details */}
    <div className="p-6">
      <div className="flex justify-between items-center mb-4">
        {/* First Name */}
        <div>
          <h2 className="text-2xl font-semibold">{profile?.first_name || 'First Name'}</h2>
          {/* Username Under First Name */}
          <h2 className="text-sm text-teal-800 opacity-75 shadow-sm">{profile ? `@${profile.username}` : '@username'}</h2>
        </div>

        {/* Edit Profile Button */}
        <Link
          to="/edit-profile"
          className="text-blue-500 border border-blue-500 px-4 py-2 rounded-full hover:bg-blue-500 hover:text-white"
        >
          Edit Profile
        </Link>
      </div>

      <p className="text-gray-600">{profile?.bio || 'No bio available'}</p>

      {/* Post Box */}
      <div className="mt-6">
        <textarea
          className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:border-blue-500"
          placeholder="What's on your mind?"
          value={newPost}
          onChange={(e) => setNewPost(e.target.value)}
        />
        <button
          onClick={handlePostSubmit}
          className="mt-2 bg-blue-500 text-white py-2 px-4 rounded-md shadow-lg hover:bg-blue-600"
        >
          Post
        </button>
      </div>

      {/* Feed Section */}
      <div className="mt-8">
        <h3 className="text-xl font-semibold mb-4">Your Posts</h3>
        {posts.length > 0 ? (
          posts.map((post) => (
            <div key={post.id} className="border p-4 mb-4 rounded-md shadow-sm">
              {post.content}
            </div>
          ))
        ) : (
          <p>No posts available.</p>
        )}
      </div>
    </div>

    {/* Modal for Image Cropping */}
    <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="modal" overlayClassName="modal-overlay">
      {modalImage ? (
        <img src={modalImage} alt="Full view" className="max-w-full max-h-full" />
      ) : (
        <>
          <ReactCrop
            src={croppingType === 'cover' ? coverImage : profileImage}
            crop={crop}
            onChange={handleCropChange}
            onComplete={handleCropComplete}
          />
          <button onClick={() => handleImageUpload(croppingType)} className="mt-4 bg-green-500 text-white py-2 px-4 rounded-md shadow-lg hover:bg-green-600">
            Save Crop
          </button>
        </>
      )}
    </Modal>
  </div>
  );
};

export default ProfilePage;
