import React, { useEffect, useState, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage, createChatRoom, fetchChatRooms, fetchMessages,sendMessage } from '../features/auth/chatSlice';
import { FaSmile, FaTimes } from 'react-icons/fa'; // Emoji toggle icons
import Picker from '@emoji-mart/react'; // Emoji picker component
import data from '@emoji-mart/data';
const ChatDialog = ({ roomName, user }) => {
    console.log(`roomName: ${roomName}`);
    const dispatch = useDispatch();
    const token = useSelector((state) => state.auth.token);
    const messages = useSelector((state) => state.chat.messages);
    const [messageInput, setMessageInput] = useState('');
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);
    const socketRef = useRef(null); // Using useRef for socket
    const chatRooms = useSelector((state) => state.chat.rooms);
    const currentUser = useSelector((state) => state.user.user);
    const room = useSelector((state)=>state.chat.rooms)
    const messagesEndRef = useRef(null);
    const [localStream, setLocalStream] = useState(null);
    const [remoteStream, setRemoteStream] = useState(null);
    const peerConnectionRef = useRef(null);
    const [inCall, setInCall] = useState(false);
    const [incomingCall, setIncomingCall] = useState(false);
    const [incomingCallerName, setIncomingCallerName] = useState(null);

    let iceCandidatesBuffer = [];
    console.log(`roomsssss${room}`)
    console.log(messages)
    console.log(user.id,currentUser.id)

    const startVideoCall = async () => {
      if (inCall) {
        console.log("Already in a call");
        return; // Prevent starting a new call if already in a call
      }
  
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
        setLocalStream(stream);
        setInCall(true);
  
        // Initialize peer connection
        peerConnectionRef.current = new RTCPeerConnection();
  
        // Add local stream tracks to the peer connection
        stream.getTracks().forEach(track => {
          peerConnectionRef.current.addTrack(track, stream);
        });
  
        // Handle remote stream
        peerConnectionRef.current.ontrack = (event) => {
          const [remoteStream] = event.streams;
          setRemoteStream(remoteStream);
        };
  
        // Handle ICE candidates
        peerConnectionRef.current.onicecandidate = (event) => {
          if (event.candidate) {
            socketRef.current.send(JSON.stringify({ candidate: event.candidate }));
          }
        };
  
        // Create offer and set local description
        const offer = await peerConnectionRef.current.createOffer();
        await peerConnectionRef.current.setLocalDescription(offer);
  
        // Send offer to the other user
        socketRef.current.send(JSON.stringify({
          type: 'video-call-offer',
          from: currentUser.id,
          to: user.id,
          offer: offer,
        }));
  
      } catch (error) {
        console.error('Error accessing media devices.', error);
      }
    };
  
    const acceptCall = async (data) => {
      setIncomingCall(false);
      try {
        await peerConnectionRef.current.setRemoteDescription(new RTCSessionDescription(data.offer));
        const answer = await peerConnectionRef.current.createAnswer();
        await peerConnectionRef.current.setLocalDescription(answer);
  
        // Send answer back to the caller
        socketRef.current.send(JSON.stringify({
          type: 'video-call-answer',
          from: currentUser.id,
          to: data.from,
          answer: answer,
        }));
      } catch (error) {
        console.error("Error accepting call:", error);
      }
    };
  
    const rejectCall = (from) => {
      setIncomingCall(false);
      socketRef.current.send(JSON.stringify({
        type: 'video-call-rejected',
        from: currentUser.id,
        to: from,
      }));
    };
  
    const endCall = () => {
      // Stop all tracks in the local stream
      localStream.getTracks().forEach(track => track.stop());
  
      // Close the peer connection
      if (peerConnectionRef.current) {
        peerConnectionRef.current.close();
        peerConnectionRef.current = null;
      }
  
      // Clean up state
      setLocalStream(null);
      setRemoteStream(null);
      setInCall(false);
    };
  

    useEffect(() => {
        if (roomName && token) {

            dispatch({ type: 'chat/resetMessages' });
            // Step 1: Fetch all chat rooms to check if the room exists
            dispatch(fetchChatRooms({ token })).then((action) => {
                const roomExists = chatRooms.some(room => room.name === roomName);

                if (roomExists) {
                    // Room exists, fetch messages for this room
                    dispatch(fetchMessages({ roomName, token }));
                } else {
                    // Room doesn't exist, create it first
                    dispatch(createChatRoom({ roomName,participants:[user.id,currentUser.id], token }))
                        .then(() => {
                            // After creating the room, fetch the messages
                            dispatch(fetchMessages({ roomName, token }));
                        });
                }
            });
        }
       


        // WebSocket connection setup
        socketRef.current = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/?token=${token}`);

        socketRef.current.onopen = () => {
            console.log("WebSocket connection opened");
        };
        
        // socketRef.current.onmessage = (e) => {
        //     const data = JSON.parse(e.data);
        //     console.log("WebSocket data received:", data); 
        //     if (data && data.message && data.sender) {
        //         dispatch(addMessage({
        //             sender: data.sender,
        //             message: data.message,
        //             timestamp: data.timestamp || new Date().toISOString(),  // Use provided timestamp or current time if not available
        //         }));
        //     }
        //     // dispatch(addMessage({
        //     //     sender: data.sender,
        //     //     message: data.message,
        //     //     timestamp: new Date().toISOString(),  // Or use the timestamp from data if available
        //     // }));
        // };
        let iceCandidatesBuffer = [];
        socketRef.current.onmessage = async (e) => {
          const data = JSON.parse(e.data);
          console.log("WebSocket data received:", data);

          if (!peerConnectionRef.current) {
            peerConnectionRef.current = new RTCPeerConnection();

            if (localStream) {
                localStream.getTracks().forEach(track => {
                    peerConnectionRef.current.addTrack(track, localStream);
                });
            }
        }

          if (data.type === 'video-call-offer') {
            // Incoming call logic
            setIncomingCallerName(data.from);
            setIncomingCall(true);
          } else if (data.type === 'video-call-rejected') {
            alert(`${data.from} has rejected the call.`);
            endCall();
          } else if (data.type === 'video-call-answer') {
            if (peerConnectionRef.current) {
              try {
                  await peerConnectionRef.current.setRemoteDescription(new RTCSessionDescription(data.answer));
                  iceCandidatesBuffer.forEach(candidate => peerConnectionRef.current.addIceCandidate(new RTCIceCandidate(candidate)));
                  iceCandidatesBuffer = [];
              } catch (error) {
                  console.error("Error handling answer:", error);
              }
          } else {
              console.error("Peer connection is not initialized.");
          }
          }else if (data.type === 'offer') {
            await peerConnectionRef.current.setRemoteDescription(new RTCSessionDescription(data.offer));
            const answer = await peerConnectionRef.current.createAnswer();
            await peerConnectionRef.current.setLocalDescription(answer);
            socketRef.current.send(JSON.stringify({ type: 'answer', answer }));
    
            // Add buffered ICE candidates
            iceCandidatesBuffer.forEach(candidate => peerConnectionRef.current.addIceCandidate(new RTCIceCandidate(candidate)));
            iceCandidatesBuffer = [];
          } else if (data.candidate) {
            if (peerConnectionRef.current.remoteDescription) {
              // If remote description is already set, add the candidate
              await peerConnectionRef.current.addIceCandidate(new RTCIceCandidate(data.candidate));
            } else {
              // Buffer the candidate if the remote description is not set
              iceCandidatesBuffer.push(data.candidate);
            }
          } else if (data.message && data.sender) {
              dispatch(addMessage({
                  sender: data.sender,
                  message: data.message,
                  timestamp: data.timestamp || new Date().toISOString(),
              }));
          }
      };

        
        socketRef.current.onclose = (event) => {
            console.log('WebSocket closed. Attempting to reconnect...');
            console.error("WebSocket closed unexpectedly:", event.reason);
        };
        

        return () => {
            socketRef.current.close();
        };
    }, [roomName, token, dispatch]);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);


    const handleSendMessage = () => {
        if (messageInput && socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
            socketRef.current.send(JSON.stringify({
                message: messageInput,
                user_id: user.id,
            }));
            console.log(`roomname=${roomName},message:${messageInput}`)
           // dispatch(sendMessage({ roomName, message: messageInput, token }));
            setMessageInput('');
        }
    };
    const formatTimestamp = (timestamp) => {
        const date = new Date(timestamp);
        return date.toLocaleTimeString([], { hour: 'numeric', minute: '2-digit', hour12: true });
    };
    const handleAddEmoji = (emoji) => {
      setMessageInput(prevInput => prevInput + emoji.native); // Append emoji to message input
  };
  
    return (
        <div className="flex flex-col h-full border-2 border-gray-300 rounded-lg shadow-lg ">
        {/* Header */}
        <div className="flex items-center p-4 bg-blue-500 text-white sticky top-0 z-10 mt-16">
          <img
            src={user.profile_pic || 'https://via.placeholder.com/800x500'}
            alt={user.username}
            className="w-10 h-10 rounded-full mr-3 object-cover"
          />
          <span className="text-lg font-semibold">{user.username}</span>
          <div className={`ml-2 w-3 h-3 rounded-full ${user.is_online ? 'bg-green-500' : 'bg-gray-500'}`}></div>
                <span className="ml-1 text-sm">
                    {user.is_online ? 'Online' : 'Offline'}
                </span>
                <button 
                  onClick={startVideoCall} 
                  className="ml-auto text-white bg-green-500 rounded-full p-2"
                >
                  ðŸ“¹ Video Call
                </button>
        </div>
      
        {/* Chat messages container */}
        <div className="flex-1 overflow-y-auto bg-gray-100 p-4">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`p-2 rounded-lg mb-2 ${
                currentUser.id === msg.sender
                  ? 'bg-blue-500 text-white self-end'
                  : 'bg-gray-400  text-white self-end'
              }`}
              style={{
                maxWidth: '50%',
                marginLeft: currentUser.id === msg.sender ? 'auto' : '0',
              }}
            >
              <span>{msg.message}</span>
              <div className="text-xs text-orange-50 mt-1 text-right">
                {formatTimestamp(msg.timestamp)}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

            {/* Incoming Call Notification */}
              {incomingCall && (
                <div className="incoming-call-notification">
                  <p>{incomingCallerName} is calling...</p>
                  <button onClick={() => acceptCall(incomingCallerName)}>Accept</button>
                  <button onClick={() => rejectCall(incomingCallerName)}>Reject</button>
                </div>
              )}
              
        {/* Message input */}
        <div className="flex items-center p-2 border-t bg-white">
               {/* Emoji Picker Button */}
               <button
                    onClick={() => setShowEmojiPicker(!showEmojiPicker)}
                    className="text-gray-500 hover:text-gray-800 mr-2"
                >
                    {showEmojiPicker ? <FaTimes size={20} /> : <FaSmile size={20} />}
                </button>

                {showEmojiPicker && (
                    <div className="absolute bottom-12 left-0 z-10">
                        <Picker data={data} onEmojiSelect={handleAddEmoji} />
                    </div>
                )}
          <input
            type="text"
            className="flex-1 border rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={messageInput}
            onChange={(e) => setMessageInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
            placeholder="Type your message..."
          />
          <button
            onClick={handleSendMessage}
            className="ml-2 bg-blue-500 text-white rounded-lg px-4 py-2 hover:bg-blue-600 transition duration-200"
          >
            Send
          </button>
        </div>

        {localStream && (
  <div className="fixed bottom-0 right-0 p-2 bg-gray-800 text-white z-50">
    <video
      playsInline
      autoPlay
      muted
      ref={(video) => {
        if (video) video.srcObject = localStream;
      }}
      className="w-40 h-40 rounded-lg"
    />
    <button
      className="text-red-500 mt-2"
      onClick={() => {
        // Stop local stream
        localStream.getTracks().forEach((track) => track.stop());
        setLocalStream(null);
        setRemoteStream(null);
        setInCall(null)
      }}
    >
      Close
    </button>
  </div>
)}

{remoteStream && (
  <div className="fixed bottom-0 right-52 p-2 bg-gray-800 text-white z-50">
    <video
      playsInline
      autoPlay
      ref={(video) => {
        if (video) video.srcObject = remoteStream;
      }}
      className="w-40 h-40 rounded-lg"
    />
  </div>
)}

      </div>
      
    );
};

export default ChatDialog;
