// src/layouts/Layout.jsx
import React, { useState } from 'react';
import { Outlet } from 'react-router-dom';
import { FaUser, FaBars, FaChevronLeft, FaChevronRight } from 'react-icons/fa';

const Layout = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside
        className={`bg-gray-800 text-white ${
          isSidebarOpen ? 'block' : 'hidden md:block' // Keep sidebar visible on large screens
        } transition-all duration-300 ease-in-out fixed md:relative inset-y-0 left-0 transform md:translate-x-0 z-30 md:w-64`}
      >
        {/* Hamburger Button for Mobile */}
        <div className="flex justify-end p-4 md:hidden">
          {/* Visible only on smaller screens */}
          <button onClick={toggleSidebar} className="text-white">
            <FaBars />
          </button>
        </div>

        {/* Sidebar Header with Toggle Button */}
        <div className="flex justify-between items-center p-4">
          {/* Toggle button for large screens */}
          <button
            onClick={toggleSidebar}
            className="text-white hidden md:block"
          >
            {isSidebarOpen ? <FaChevronLeft /> : <FaChevronRight />}
          </button>
        </div>

        {/* Sidebar Links */}
        {isSidebarOpen && (
          <nav className="mt-4 px-2 space-y-2">
            {/* Profile Link with Icon */}
            <a href="#" className="flex items-center py-2 px-3 hover:bg-blue-700 rounded">
              <FaUser className="mr-2" /> {/* Small rounded icon */}
              Profile
            </a>
            <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
              Dashboard
            </a>
            <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
              Settings
            </a>
            <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
              Logout
            </a>
          </nav>
        )}
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col ml-0 md:ml-0 transition-all duration-300 ease-in-out">
        {/* Header */}
        <header className="bg-white shadow-md flex items-center justify-between px-6 py-4">
          <h1 className="text-2xl font-bold">My Application</h1>
        </header>

        {/* Content Area */}
        <main className="flex-grow p-6 bg-gray-100 overflow-auto">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default Layout;
