import React, { useEffect, useState, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage, createChatRoom, fetchChatRooms, fetchMessages,sendMessage } from '../features/auth/chatSlice';
import { FaSmile, FaTimes,FaPaperclip  } from 'react-icons/fa'; // Emoji toggle icons
import Picker from '@emoji-mart/react'; // Emoji picker component
import data from '@emoji-mart/data';
const ChatDialog = ({ roomName, user }) => {
    console.log(`roomName: ${roomName}`);
    const dispatch = useDispatch();
    const token = useSelector((state) => state.auth.token);
    const messages = useSelector((state) => state.chat.messages);
    const [messageInput, setMessageInput] = useState('');
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);
    const socketRef = useRef(null); // Using useRef for socket
    const chatRooms = useSelector((state) => state.chat.rooms);
    const currentUser = useSelector((state) => state.user.user);
    const room = useSelector((state)=>state.chat.rooms)
    const messagesEndRef = useRef(null);
    const [localStream, setLocalStream] = useState(null);
    const [remoteStream, setRemoteStream] = useState(null);
    const peerConnectionRef = useRef(null);
    const [inCall, setInCall] = useState(false);
    const [incomingCall, setIncomingCall] = useState(false);
    const [incomingCallerName, setIncomingCallerName] = useState(null);
    const [offerdata, setOfferdata] = useState(null);
    const [file, setFile] = useState(null);
    const [messageType, setMessageType] = useState('text');
    const [filePreview, setFilePreview] = useState(null);
    let iceCandidatesBuffer = [];
    console.log(`roomsssss${room}`)
    console.log(messages)
    console.log(user.id,currentUser.id)

    const startVideoCall = async () => {
      if (inCall) {
          console.log("Already in a call");
          return;
      }

      try {
          const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
          setLocalStream(stream);
          setInCall(true);

          peerConnectionRef.current = new RTCPeerConnection({
            iceServers: [
                {
                    urls: 'stun:stun.l.google.com:19302',
                },
            ],
        });

          // Add local stream tracks to peer connection
          stream.getTracks().forEach(track => {
              peerConnectionRef.current.addTrack(track, stream);
          });

          peerConnectionRef.current.ontrack = (event) => {
              const [remoteStream] = event.streams;
              setRemoteStream(remoteStream);
          };

          peerConnectionRef.current.onicecandidate = (event) => {
              if (event.candidate) {
                  socketRef.current.send(JSON.stringify({ candidate: event.candidate }));
              }
          };

          const offer = await peerConnectionRef.current.createOffer();
          await peerConnectionRef.current.setLocalDescription(offer);

          // Send offer to the other user
          socketRef.current.send(JSON.stringify({
              type: 'video-call-offer',
              from: currentUser.id,
              to: user.id !== currentUser.id ? user.id : currentUser.id,
              offer: offer,
          }));
      } catch (error) {
          console.error('Error accessing media devices.', error);
      }
  };
  const markMessagesAsRead = (messageId) => {
    if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
        socketRef.current.send(JSON.stringify({
            type: 'message_read',
            message_id: messageId,
            user_id: currentUser.id,
        }));
    }
};
  
  const acceptCall = async (data) => {
    //setIncomingCallerName(data.to);
    console.log(`datafrom:${data.from}`)
    console.log(`data.offer:${data.offer.sdp}`)
    console.log(`incomingCallerName:${incomingCallerName}`)
    setIncomingCall(false);
    try {
        await peerConnectionRef.current.setRemoteDescription(new RTCSessionDescription({
          type: 'offer',
          sdp: data.offer.sdp, // Use the received offer's sdp
          }));
          const answer = await peerConnectionRef.current.createAnswer();
          await peerConnectionRef.current.setLocalDescription(answer);

          // Send answer back to the caller
          socketRef.current.send(JSON.stringify({
              type: 'video-call-answer',
              answer: answer,
              offer:data.offer,
              to: data.to
          }));
       
          } catch (error) {
              console.error("Error accepting call:", error);
          }
};

  
    const rejectCall = (from) => {
      setIncomingCall(false);
      socketRef.current.send(JSON.stringify({
        type: 'video-call-rejected',
        from: currentUser.id,
        to: from,
      }));
    };
  
    
    const endCall = () => {
      if (localStream) {
          localStream.getTracks().forEach(track => track.stop());
      }

      if (peerConnectionRef.current) {
          peerConnectionRef.current.close();
          peerConnectionRef.current = null;
      }

      setLocalStream(null);
      setRemoteStream(null);
      setInCall(false);
  };
  

    useEffect(() => {
        if (roomName && token) {

            dispatch({ type: 'chat/resetMessages' });
            // Step 1: Fetch all chat rooms to check if the room exists
            dispatch(fetchChatRooms({ token })).then((action) => {
                const roomExists = chatRooms.some(room => room.name === roomName);

                if (roomExists) {
                    // Room exists, fetch messages for this room
                    dispatch(fetchMessages({ roomName, token }));
                } else {
                    // Room doesn't exist, create it first
                    dispatch(createChatRoom({ roomName,participants:[user.id,currentUser.id], token }))
                        .then(() => {
                            // After creating the room, fetch the messages
                            dispatch(fetchMessages({ roomName, token }));
                        });
                }
            });
        }
       // WebSocket connection setup
        socketRef.current = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/?token=${token}`);

        socketRef.current.onopen = () => {
            console.log("WebSocket connection opened");
        };
        
        let iceCandidatesBuffer = [];
        socketRef.current.onmessage = async (e) => {
          const data = JSON.parse(e.data);
          console.log("WebSocket data received:", data);

          if (!peerConnectionRef.current) {
            peerConnectionRef.current = new RTCPeerConnection();

            if (localStream) {
                localStream.getTracks().forEach(track => {
                    peerConnectionRef.current.addTrack(track, localStream);
                });
            }
        }

        if (data.type === 'video-call-offer') {
          if (data.to === currentUser.id) {
              setOfferdata(data);
              setIncomingCall(true);
              console.log(`data:${data}`)
              console.log(`IncomingCallerName:${data.from}`)
              console.log(`to:${data.to}`)
              setIncomingCallerName(data.from);
          }
        } else if (data.type === 'video-call-answer') {
                if (peerConnectionRef.current.signalingState === 'stable') {
                  await peerConnectionRef.current.setRemoteDescription(new RTCSessionDescription({
                      type: 'answer',
                      sdp: data.answer.sdp,
                  }));
              } else {
                  console.log('Peer connection is not in a stable state. Cannot set remote description.');
              }
              // Process buffered ICE candidates
              iceCandidatesBuffer.forEach(candidate => {
                  peerConnectionRef.current.addIceCandidate(new RTCIceCandidate(candidate)).catch(error => {
                      console.error('Error adding received ICE candidate', error);
                  });
              });
              iceCandidatesBuffer = [];  // Clear buffer after processing
            } else if (data.candidate) {
              if (incomingCall && peerConnectionRef.current.remoteDescription) {
                await peerConnectionRef.current.addIceCandidate(new RTCIceCandidate(data.candidate)).catch(error => {
                    console.error('Error adding received ICE candidate', error);
                });
                } else {
                    iceCandidatesBuffer.push(data.candidate); // Buffer candidate until remote description is set
                }
            } else if (data.message && data.sender) {
              dispatch(addMessage({
                  sender: data.sender,
                  message: data.message,
                  message_type: data.message_type,
                  file: data.file,
                  timestamp: data.timestamp || new Date().toISOString(),
                  id: data.message_id,  // Include the message ID
                  read_by: data.read_by || []
              }));
          }else if (data.file && data.sender) {
            dispatch(addMessage({
                sender: data.sender,
                message: data.message,
                message_type: data.message_type,
                file: data.file,
                timestamp: data.timestamp || new Date().toISOString(),
                id: data.message_id,  // Include the message ID
                read_by: data.read_by || []
            }));
        }
      };

        
        socketRef.current.onclose = (event) => {
            console.log('WebSocket closed. Attempting to reconnect...');
            console.error("WebSocket closed unexpectedly:", event.reason);
        };
        

        return () => {
            socketRef.current.close();
        };
    }, [roomName, token, dispatch]);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);
    useEffect(() => {
        messages.forEach(msg => {
            if (msg.sender !== currentUser.id && !msg.read_by.includes(currentUser.id)) {
                markMessagesAsRead(msg.id);
            }
        });
    }, [messages]);

    const handleSendMessage = async  () => {
        if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
        try {
            const result = await dispatch(sendMessage({ roomName, message:messageInput, messageType, file, token })).unwrap();
            console.log(`result:${result}`)
            console.log(`result id:${result.id}`)
            socketRef.current.send(JSON.stringify({
                type: 'message_sent',
                sender: currentUser.id,
                message_id: result.id, 
                timestamp: new Date().toISOString(),
              }));
            setMessageInput('');
            setFile(null);
            setMessageType('text'); 
          } catch (err) {
            console.error(err);
          }
        }
     
    };


   
    const formatTimestamp = (timestamp) => {
        const date = new Date(timestamp);
        return date.toLocaleTimeString([], { hour: 'numeric', minute: '2-digit', hour12: true });
    };
    const handleAddEmoji = (emoji) => {
      setMessageInput(prevInput => prevInput + emoji.native); // Append emoji to message input
  };
  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    if (selectedFile) {
        setFile(selectedFile);
        const fileType = selectedFile.type;
        const maxVideoSize = 100 * 1024 * 1024; 
        // Set message type based on the file type
        if (fileType.startsWith('image/')) {
            setMessageType('image');
            setFilePreview(URL.createObjectURL(selectedFile)); // Preview the image
        } else if (fileType === 'image/gif') {
            setMessageType('gif');
            setFilePreview(URL.createObjectURL(selectedFile)); // Preview the GIF
        } else if (fileType.startsWith('video/')) {
            if (selectedFile.size > maxVideoSize) {
                alert('Video file size must be smaller than 100MB.');
                return; 
            }
            setMessageType('video');
            setFilePreview(URL.createObjectURL(selectedFile)); // Preview the video
        } else {
            setMessageType('file');
            setFilePreview(null); // No preview for other file types
        }
    }
};
  
    return (
        <div className="flex flex-col h-full border-2 border-gray-300 rounded-lg shadow-lg ">
        {/* Header */}
        <div className="flex items-center p-4 bg-blue-500 text-white sticky top-0 z-10 mt-16">
          <img
            src={user.profile_pic || 'https://via.placeholder.com/800x500'}
            alt={user.username}
            className="w-10 h-10 rounded-full mr-3 object-cover"
          />
          <span className="text-lg font-semibold">{user.username}</span>
          <div className={`ml-2 w-3 h-3 rounded-full ${user.is_online ? 'bg-green-500' : 'bg-gray-500'}`}></div>
                <span className="ml-1 text-sm">
                    {user.is_online ? 'Online' : 'Offline'}
                </span>
                <button 
                  onClick={startVideoCall} 
                  className="ml-auto text-white bg-green-500 rounded-full p-2"
                >
                  ðŸ“¹ Video Call
                </button>
        </div>

            {/* File Preview Section */}
            {filePreview && (
                <div className="flex items-center justify-center p-4 border-b">
                    {messageType === 'image' && (
                        <img src={filePreview} alt="Preview" className="w-32 h-32 object-cover rounded-lg" />
                    )}
                    {messageType === 'video' && (
                        <video src={filePreview} controls className="w-32 h-32 object-cover rounded-lg" />
                    )}
                    {messageType === 'gif' && (
                        <img src={filePreview} alt="Preview" className="w-32 h-32 object-cover rounded-lg" />
                    )}
                </div>
            )}
      
        {/* Chat messages container */}
        <div className="flex-1 overflow-y-auto bg-gray-100 p-4">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`p-2 rounded-lg mb-2 ${
                currentUser.id === msg.sender
                  ? 'bg-blue-500 text-white self-end'
                  : 'bg-gray-400  text-white self-end'
              }`}
              style={{
                maxWidth: '50%',
                marginLeft: currentUser.id === msg.sender ? 'auto' : '0',
              }}
            >
                    {msg.message_type === 'text' ? ( 
                        <span>{msg.message}</span>
                    ) : msg.file ? ( 
                        // Check the media type to render images, GIFs, or videos
                        <>
                        {msg.message_type === 'image' ? (
                            <img
                                src={msg.file}
                                alt="Image"
                                className="max-w-full w-[300px] h-auto object-cover rounded-lg shadow-md"
                            />
                        ) : msg.message_type === 'video' ? (
                            <video
                                src={msg.file}
                                controls
                                className="max-w-full w-[300px] h-auto rounded-lg shadow-md"
                            />
                            ) : msg.message_type === 'gif' ? (
                                <img
                                    src={msg.file}
                                    alt="GIF"
                                    className="max-w-full w-[300px] h-auto object-cover rounded-lg shadow-md"
                                />
                            ) : (
                                <span>No file available</span> // Optional fallback for unrecognized file types
                            )}
                        </>
                    ) : (
                        <span>No message available</span>
                    )}
                    <div className="message-status flex items-center mt-1">
                        {/* Sent, Delivered, Read Status */}
                        {msg.read_by?.includes(currentUser.id) ? (
                            <div className="flex items-center">
                                <span className="text-green-500" title="Read">
                                    âœ”âœ”
                                </span>
                                <span className="ml-1 text-xs text-white">Read</span>
                            </div>
                        ) : msg.read_by?.length > 0 ? (
                            <div className="flex items-center">
                                <span className="text-white" title="Delivered">
                                    âœ”
                                </span>
                                <span className="ml-1 text-xs  text-white">Delivered</span>
                            </div>
                        ) : (
                            <div className="flex items-center">
                                <span className=" text-white" title="Sent">
                                    âœ”
                                </span>
                                <span className="ml-1 text-xs  text-white">Sent</span>
                            </div>
                        )}
                       
                        </div>
              <div className="text-xs text-orange-50 mt-1 text-right">
                {formatTimestamp(msg.timestamp)}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

           
              
        {/* Message input */}
        <div className="flex items-center p-2 border-t bg-white">
               {/* Emoji Picker Button */}
               <button
                    onClick={() => setShowEmojiPicker(!showEmojiPicker)}
                    className="text-gray-500 hover:text-gray-800 mr-2"
                >
                    {showEmojiPicker ? <FaTimes size={20} /> : <FaSmile size={20} />}
                </button>

                {showEmojiPicker && (
                    <div className="absolute bottom-12 left-0 z-10">
                        <Picker data={data} onEmojiSelect={handleAddEmoji} />
                    </div>
                )}
                
          <input
            type="text"
            className="flex-1 border rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={messageInput}
            onChange={(e) => setMessageInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
            placeholder="Type your message..."
          />
          <label className="ml-2">
                    <FaPaperclip size={20} />
                    <input type="file" onChange={handleFileChange} className="hidden" accept="image/*,video/*,.gif" />
            </label>
            
          <button
            onClick={handleSendMessage}
            className="ml-2 bg-blue-500 text-white rounded-lg px-4 py-2 hover:bg-blue-600 transition duration-200"
          >
            Send
          </button>
        </div>

        {incomingCall && (
                <div className="fixed bottom-4 left-1/2 transform -translate-x-1/2 bg-gray-700 text-white p-4 rounded-lg">
                    <p>{incomingCallerName} is calling...</p>
                    <button onClick={() => acceptCall(offerdata)} className="bg-green-500 p-2 rounded-lg">Accept</button>
                    <button onClick={() => setIncomingCall(false)} className="bg-red-500 p-2 rounded-lg">Decline</button>
                </div>
            )}

            {/* Video Call Stream */}
            {inCall && (
                <div className="video-call">
                    <h2>Video Call in Progress</h2>
                    <video autoPlay playsInline ref={(video) => { if (video) video.srcObject = localStream; }} />
                    <video autoPlay playsInline ref={(video) => { if (video) video.srcObject = remoteStream; }} />
                    <button onClick={endCall}>End Call</button>
                </div>
            )}
        

      </div>
      
    );
};

export default ChatDialog;
