import React from 'react';
import './App.css';
import Login from './components/Login';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import store from './store';
import { Provider } from 'react-redux';
import SignupPage from './pages/SignupPage';
import Layout from './layouts/Layouts';
import { ToastContainer } from 'react-toastify';
import VerificationWaitingPage from './pages/VerificationWaitingPage ';
import AdminDashboard from './admin/dashboard';
import ProtectedRoute from './components/ProtectedRoute';
import PasswordResetRequest from './components/PasswordResetRequest';
import PasswordReset from './components/PasswordReset';
import AdminUserList from './admin/AdminUserList';
import ProfilePage from './pages/ProfilePage';
import EditProfilePage from './pages/EditProfilePage';
import ProfileDetailsPage from './pages/ProfileDetailsPage'
import { GoogleOAuthProvider } from '@react-oauth/google';
import { GOOGLE_CLIENT_ID } from './config';
import ProfileView from './pages/ProfileView';
import UserProfilePage from './pages/ProfileView';
import useNotifications from './components/NotificationService';




function App() {
  const [notifications, setNotifications] = useState([]);

  const handleReceiveNotification = (message) => {
    setNotifications((prev) => [...prev, message]);
  };

  useNotifications(handleReceiveNotification); // 
  return (  

    <Provider store={store}>
     <GoogleOAuthProvider clientId={GOOGLE_CLIENT_ID}>
    <Router>
      <ToastContainer/>
      <div className="notifications-container">
            {notifications.map((msg, index) => (
              <Notification key={index} message={msg} />
            ))}
          </div>
        <Routes>
        <Route path="/" element={<Layout />}>
        <Route path="/profile" element={<ProfilePage />} >

        </Route>

        <Route path="/edit-profile" element={<EditProfilePage />} />
        <Route path="/profile-detail" element={<ProfileDetailsPage />} />
        <Route path="/profileview/:id" element={<UserProfilePage />} />
        </Route>
        <Route path="/login" element={<Login />} />

        <Route path="/signup" element={<SignupPage />} />
        <Route path="/verification-waiting/:uidb64/:token" element={<VerificationWaitingPage />} />
        <Route
            path="/admin/dashboard"
            element={
              <ProtectedRoute isAdminRoute={true}>
                <AdminDashboard />
              </ProtectedRoute>
            }
          > 
            <Route path="users" element={<AdminUserList />} /></Route>
           <Route path="/password-reset-request" element={<PasswordResetRequest />} />
           <Route path="/password-reset/:uidb64/:token" element={<PasswordReset />} />
        </Routes>

    </Router>
    </GoogleOAuthProvider>
</Provider>



  );
}

export default App;
