// src/components/PostBox.jsx

import React, { useState, useRef } from 'react';
import { FaSmile, FaCamera, FaVideo } from 'react-icons/fa';
import EmojiPicker from 'emoji-picker-react';

import Modal from 'react-modal';
import ImageCropper from './Cropper';

Modal.setAppElement('#root');

const PostBox = ({ onPostSubmit }) => {
  const [postContent, setPostContent] = useState('');
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [selectedMedia, setSelectedMedia] = useState(null);
  const [mediaType, setMediaType] = useState('');
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [croppedImage, setCroppedImage] = useState(null);
  const cropperRef = useRef(null);

  const handleEmojiClick = (event, emojiObject) => {
    setPostContent((prevContent) => prevContent + emojiObject.emoji);
  };

  const handleMediaChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const fileType = file.type.split('/')[0];
      if (fileType === 'image') {
        // For images, open cropper modal
        const reader = new FileReader();
        reader.onload = () => {
          setSelectedMedia(reader.result);
          setMediaType('image');
          setModalIsOpen(true);
        };
        reader.readAsDataURL(file);
      } else if (fileType === 'video') {
        // For videos, set selected media
        setSelectedMedia(URL.createObjectURL(file));
        setMediaType('video');
      }
    }
  };

  const handleImageCrop = (croppedImageData) => {
    setCroppedImage(croppedImageData);
    setModalIsOpen(false);
  };

  const handlePost = () => {
    // Prepare post data
    const postData = {
      content: postContent,
      media: mediaType === 'image' ? croppedImage : selectedMedia,
      mediaType,
    };
    // Call the onPostSubmit prop to handle post submission
    onPostSubmit(postData);
    // Reset the post box
    setPostContent('');
    setSelectedMedia(null);
    setMediaType('');
    setCroppedImage(null);
  };

  return (
    <div className="p-6 bg-gray-100 rounded-lg mb-6 shadow-lg">
      <div className="relative">
        <textarea
          className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none h-24"
          placeholder="What's on your mind?"
          value={postContent}
          onChange={(e) => setPostContent(e.target.value)}
        />
        <div className="absolute bottom-3 left-3 flex items-center space-x-2">
          <button
            onClick={() => setShowEmojiPicker((prev) => !prev)}
            className="text-gray-500 hover:text-gray-700"
          >
            <FaSmile className="text-xl" />
          </button>
          <input
            type="file"
            accept="image/*"
            id="image-upload"
            onChange={handleMediaChange}
            className="hidden"
          />
          <label htmlFor="image-upload" className="text-gray-500 hover:text-gray-700 cursor-pointer">
            <FaCamera className="text-xl" />
          </label>
          <input
            type="file"
            accept="video/*"
            id="video-upload"
            onChange={handleMediaChange}
            className="hidden"
          />
          <label htmlFor="video-upload" className="text-gray-500 hover:text-gray-700 cursor-pointer">
            <FaVideo className="text-xl" />
          </label>
        </div>
        {showEmojiPicker && (
          <div className="absolute bottom-16 left-3 z-50">
            <EmojiPicker onEmojiClick={handleEmojiClick} />
          </div>
        )}
      </div>
      {/* Preview Selected Media */}
      {selectedMedia && mediaType === 'video' && (
        <video controls className="mt-4 max-w-full rounded-md">
          <source src={selectedMedia} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      )}
      {croppedImage && mediaType === 'image' && (
        <img src={croppedImage} alt="Cropped" className="mt-4 max-w-full rounded-md" />
      )}
      <button
        onClick={handlePost}
        className="mt-4 bg-blue-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-blue-600"
      >
        Post
      </button>

      {/* Modal for Image Cropping */}
      {modalIsOpen && (
        <Modal isOpen={modalIsOpen} onRequestClose={() => setModalIsOpen(false)} className="w-full max-w-2xl mx-auto mt-16">
          <ImageCropper
            imageSrc={selectedMedia}
            onCrop={handleImageCrop}
            onClose={() => setModalIsOpen(false)}
            cropperRef={cropperRef}
          />
        </Modal>
      )}
    </div>
  );
};

export default PostBox;
