import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BASE_URL } from '../config';
import { Link, useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';
const Followers = () => {
  const user = useSelector((state) => state.user.user);
  const token = useSelector((state) => state.auth.token);
  const { userId } = useParams(); 
  const [followers, setFollowers] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true);
  const encodeId = (id) => {
    return btoa(id); // Base64 encode the ID
  };
  useEffect(() => {
    // Fetch followers when component loads
    fetchFollowers();
  }, [token,user]);
  const checkUser=user.id

  const fetchFollowers = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${BASE_URL}/api/interactions/connections/?user_id=${userId}&type=followers`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setFollowers(response.data);
    } catch (error) {
      console.error('Error fetching followers:', error);
    }finally {
      setLoading(false); // End loading
    }
  };

  const handleRemoveFollower = async (userId) => {
    try {
      await axios.post(`${BASE_URL}/api/interactions/remove-follower/${userId}/`, { follower_id: user.id }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setFollowers(followers.filter(follower => follower.id !== userId));
    } catch (error) {
      console.error('Error removing follower:', error);
    }
  };

  const filteredFollowers = followers.filter(follower =>
    follower.username.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="max-w-lg mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Followers</h1>
      <input
        type="text"
        placeholder="Search followers..."
        className="mb-4 p-2 w-full border border-gray-300 rounded-md"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      {loading ? (
        <div className="flex justify-center items-center h-48">
          <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-blue-500 border-solid"></div>
        </div>
      ) : filteredFollowers.length === 0 ? (
        <div className="text-center text-gray-500">No followers until now.</div>
      ) : (
        <ul className="space-y-4">
          {filteredFollowers.map((follower) => (
            <Link
            to={`/profileview/${encodeId(follower.id)}`}
            className="text-blue-500 hover:text-blue-700"
            title="Profile"
          >
            <li key={follower.id} className="flex items-center justify-between bg-white p-4 rounded-lg shadow-md">
              <div className="flex items-center space-x-4">
                <img src={follower.profile_pic} alt="Profile" className="w-10 h-10 rounded-full object-cover" />
                <span>{follower.username}</span>
              </div>
              {checkUser == userId ? ( // Only show Remove button if user ID matches follower ID
                <button
                  onClick={() => handleRemoveFollower(follower.id)}
                  className="text-red-500 border border-red-500 px-4 py-1 rounded-md hover:bg-red-500 hover:text-white"
                >
                  Remove
                </button>
              ) : null}
            </li></Link>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Followers;
