import React, { useEffect, useRef,useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { fetchRoom } from '../features/auth/groupChatSlice';
import { fetchMessages, sendMessage } from '../features/auth/chatSlice';
import Picker from '@emoji-mart/react'; // Emoji picker component
import { FaSmile, FaPaperclip } from 'react-icons/fa';

const GroupChatBox = () => {
  const { roomId } = useParams(); 
  const token = useSelector((state)=>state.auth.token)  
  const dispatch = useDispatch();
  const { group, loading, error } = useSelector((state) => state.groupChat);
  const [messageInput, setMessageInput] = useState('');
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [file, setFile] = useState(null);
  const messagesEndRef = useRef(null);
  const messagesContainerRef = useRef(null);
  const messages = useSelector((state) => state.chat.messages);
  useEffect(() => {
    dispatch(fetchRoom({ roomId,token }));
  }, [dispatch, token]);

  useEffect(() => {
    if (roomId) {
        if(group){
      dispatch(fetchMessages({ roomName:group.group_name, token }));
    }
    }
  }, [dispatch, roomId, token]);

  useEffect(() => {
    if (roomId && group) {
      dispatch(fetchMessages({ roomName: group.group_name, token }));

      // Establish WebSocket connection
      const ws_url = `ws://localhost:8000/ws/groupchat/${roomId}/`;  // Adjust based on your backend server URL
      socketRef.current = new WebSocket(ws_url);

      socketRef.current.onopen = () => {
        console.log('WebSocket connection opened.');
      };

      socketRef.current.onmessage = (event) => {
        const data = JSON.parse(event.data);
        const { message, sender_username, sender_profile_pic } = data;

        // Dispatch received message to Redux store
        dispatch(addMessage({ 
          
        }));
      };

      socketRef.current.onclose = () => {
        console.log('WebSocket connection closed.');
      };

      return () => {
        if (socketRef.current) {
          socketRef.current.close();
        }
      };
    }
  }, [dispatch, roomId, group]);

  // Scroll to bottom when new messages are added
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  // Load older messages when scrolling to the top
  const handleScroll = (e) => {
    if (e.target.scrollTop === 0) {
      //dispatch(loadOlderMessages({ roomId, token }));
    }
  };

  const handleSendMessage = async () => {
    if (messageInput.trim() || file) {
      await dispatch(sendMessage({ roomId, message: messageInput, file, token }));
      setMessageInput('');
      setFile(null);
    }
  };

  const handleAddEmoji = (emoji) => {
    setMessageInput((prev) => prev + emoji.native);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }
  return (
<div className="group-chat-box max-w-4xl mx-auto bg-white shadow-lg rounded-lg overflow-hidden h-full flex flex-col">
  <div className="chat-header bg-blue-500 text-white px-4 py-3">
    {group && group.group_name ? (
      <h2 className="text-xl font-bold">Chat Room: {group.group_name}</h2>
    ) : (
      <h2 className="text-xl font-bold">Chat Room Details Not Available</h2>
    )}
  </div>

  <div
    className="messages-container px-4 py-2 space-y-4 flex-1 overflow-y-auto"
    ref={messagesContainerRef}
    onScroll={handleScroll}
  >
    {messages.map((msg) => (
      <div
        key={msg.id}
        className={`message p-3 rounded-lg ${
          msg.isOwn ? 'bg-blue-100 self-end' : 'bg-gray-100'
        }`}
      >
        <div className="message-sender text-sm font-semibold text-gray-600">
          {msg.sender}
        </div>
        <div className="message-text text-gray-900 mt-1">
          {msg.message}
        </div>
      </div>
    ))}
    <div ref={messagesEndRef} />
  </div>

  <div className="input-container bg-gray-50 flex items-center px-4 py-3 border-t space-x-2">
    <div className="relative emoji-picker-container">
      {showEmojiPicker && (
        <div className="absolute bottom-full mb-2">
          <Picker onEmojiSelect={handleAddEmoji} />
        </div>
      )}
      <button
        className="text-xl text-gray-600 hover:text-blue-500"
        onClick={() => setShowEmojiPicker((prev) => !prev)}
      >
        <FaSmile />
      </button>
    </div>

    <input
      type="text"
      value={messageInput}
      onChange={(e) => setMessageInput(e.target.value)}
      placeholder="Type your message..."
      className="message-input flex-1 px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
    />

    <input
      type="file"
      onChange={handleFileChange}
      className="hidden"
      id="file-upload"
    />
    <label
      htmlFor="file-upload"
      className="cursor-pointer text-xl text-gray-600 hover:text-blue-500"
    >
      <FaPaperclip />
    </label>

    <button
      onClick={handleSendMessage}
      className="send-button bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 focus:outline-none"
    >
      Send
    </button>
  </div>
</div>


  );
};

export default GroupChatBox;
