import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import { BASE_URL } from '../config';

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);
  const [posts, setPosts] = useState([]); // State for posts
  const [newPost, setNewPost] = useState(''); // State for new post
  const [coverImage, setCoverImage] = useState(null); // State for cover image upload
  const [profileImage, setProfileImage] = useState(null); // State for profile image upload

  const user = useSelector((state) => state.user.user); // Access user from Redux store
  const token = useSelector((state) => state.auth.token); // Access token from Redux store

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/profile/${user?.id}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProfile(response.data);
      } catch (err) {
        setError(err.response?.data?.detail || 'An error occurred while fetching the profile.');
      }
    };

    const fetchPosts = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/posts/${user?.id}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setPosts(response.data);
      } catch (err) {
        setError(err.response?.data?.detail || 'An error occurred while fetching posts.');
      }
    };

    if (user && token) {
      fetchProfile();
      fetchPosts();
    }
  }, [user, token]);

  const handleFileChange = (event, type) => {
    const file = event.target.files[0];
    if (type === 'cover') {
      setCoverImage(file);
    } else {
      setProfileImage(file);
    }
  };

  const handleImageUpload = async (type) => {
    const formData = new FormData();
    formData.append(type === 'cover' ? 'cover_pic' : 'profile_pic', type === 'cover' ? coverImage : profileImage);

    try {
      await axios.patch(`${BASE_URL}/api/users/profile/${user?.id}/`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      setCoverImage(null);
      setProfileImage(null);
      // Re-fetch profile data after image upload
      setProfile((prevProfile) => ({
        ...prevProfile,
        [type === 'cover' ? 'cover_pic' : 'profile_pic']: URL.createObjectURL(type === 'cover' ? coverImage : profileImage),
      }));
    } catch (err) {
      setError('An error occurred while uploading the image.');
    }
  };

  const handlePostSubmit = async () => {
    try {
      await axios.post(`${BASE_URL}/api/users/posts/`, { content: newPost }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setNewPost('');
      // Re-fetch posts after new post submission
      setPosts((prevPosts) => [{ content: newPost, id: Date.now() }, ...prevPosts]);
    } catch (err) {
      setError('An error occurred while posting.');
    }
  };

  if (error) {
    return <div className="text-red-500 text-center mt-4">{error}</div>;
  }

  if (!profile) {
    return <div className="text-center mt-4">Loading...</div>;
  }

  return (
    <div className="max-w-3xl mx-auto bg-white shadow-md rounded-md overflow-hidden mt-8">
      {/* Cover Photo */}
      <div className="relative h-40 bg-cover bg-center" style={{ backgroundImage: `url(${profile.cover_pic || 'https://via.placeholder.com/800x200'})` }}>
        <input type="file" onChange={(e) => handleFileChange(e, 'cover')} className="absolute top-2 right-2" />
        <button onClick={() => handleImageUpload('cover')} className="absolute top-2 right-20 text-white bg-blue-500 px-2 py-1 rounded">
          Upload Cover
        </button>
        {/* Profile Picture */}
        <div className="absolute bottom-0 left-4 transform -translate-y-1/2">
          <img
            src={profile.profile_pic || 'https://via.placeholder.com/100'}
            alt="Profile"
            className="w-20 h-20 rounded-full border-4 border-white"
          />
          <input type="file" onChange={(e) => handleFileChange(e, 'profile')} className="mt-2" />
          <button onClick={() => handleImageUpload('profile')} className="text-white bg-blue-500 px-2 py-1 rounded">
            Upload Profile
          </button>
        </div>
      </div>

      {/* Profile Details */}
      <div className="p-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-semibold">{profile.username}</h2>
          <button className="text-blue-500 border border-blue-500 px-4 py-1 rounded-full hover:bg-blue-500 hover:text-white">
            Edit Profile
          </button>
        </div>
        <p className="text-gray-600">{profile.bio || 'No bio available'}</p>

        {/* Post Box */}
        <div className="mt-6">
          <textarea
            className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:border-blue-500"
            placeholder="What's on your mind?"
            value={newPost}
            onChange={(e) => setNewPost(e.target.value)}
          />
          <button onClick={handlePostSubmit} className="mt-2 bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600">
            Post
          </button>
        </div>

        {/* Feed Section */}
        <div className="mt-8">
          <h3 className="text-xl font-semibold mb-4">Your Posts</h3>
          {posts.length > 0 ? (
            posts.map((post) => (
              <div key={post.id} className="border p-4 mb-4 rounded-md shadow-sm">
                <p className="text-gray-800">{post.content}</p>
              </div>
            ))
          ) : (
            <p className="text-gray-500">No posts available</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
