import React, { useState, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import { BASE_URL } from '../config';
import { FaCamera } from 'react-icons/fa';
import Modal from 'react-modal';
import Cropper from 'react-cropper';
import 'cropperjs/dist/cropper.css';
import EmojiPicker from 'emoji-picker-react';
import { updateUserProfileImage } from '../features/auth/userSlice';
import { showErrorToast } from './ErroToast';
import { Link } from 'react-router-dom';


Modal.setAppElement('#root');

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);
  const [posts, setPosts] = useState([]);
  const [newPostText, setNewPostText] = useState('');
  const [coverImage, setCoverImage] = useState(null);
  const [profileImage, setProfileImage] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [modalImage, setModalImage] = useState('');
  const [imageType, setImageType] = useState(''); // 'cover' or 'profile'
  const cropperRef = useRef(null);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);

  const user = useSelector((state) => state.user.user);
  const token = useSelector((state) => state.auth.token);
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/profile/${user?.id}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProfile(response.data);
      } catch (err) {
        setError(err.response?.data?.detail || 'An error occurred while fetching the profile.');
      }
    };

    if (user && token) {
      fetchProfile();
    }
  }, [user, token]);

  const handleFileChange = (event, type) => {
    const file = event.target.files[0];
    if (file) {
      const validImageTypes = ['image/jpeg', 'image/jpg', 'image/png'];
      if (type === 'cover' || type === 'profile') {
        if (!validImageTypes.includes(file.type)) {
          showErrorToast('Please select a valid image file (JPEG, JPG, or PNG).');
          event.target.value = null;
          return;
        }
      }
      const reader = new FileReader();
      reader.onloadend = () => {
        setModalImage(reader.result);
        setImageType(type);
        setModalIsOpen(true);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleImageUpload = async () => {
    if (!cropperRef.current) return;

    const cropper = cropperRef.current.cropper;
    const canvas = cropper.getCroppedCanvas();
    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append(imageType === 'cover' ? 'cover_pic' : 'profile_pic', blob, 'cropped-image.jpg');

      try {
        await axios.patch(`${BASE_URL}/api/users/profile/update/`, formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
        });

        const updatedImageURL = URL.createObjectURL(blob);
        setProfile((prevProfile) => ({
          ...prevProfile,
          [imageType === 'cover' ? 'cover_pic' : 'profile_pic']: updatedImageURL,
        }));

        if (imageType === 'profile') {
          dispatch(updateUserProfileImage(updatedImageURL));
        }

        closeModal();
      } catch (err) {
        setError('An error occurred while uploading the image.');
      }
    }, 'image/jpeg');
  };

  const handlePostSubmit = async () => {
    if (!newPostText.trim()) {
      setError('Post content cannot be empty.');
      return;
    }

    try {
      setPosts((prevPosts) => [
        ...prevPosts,
        { id: Date.now(), content: newPostText },
      ]);
      setNewPostText('');
    } catch (err) {
      setError('An error occurred while submitting the post.');
    }
  };

  const handleEmojiClick = (event, emojiObject) => {
    setNewPostText(newPostText + emojiObject.emoji);
    setShowEmojiPicker(false);
  };

  const openModal = (type) => {
    setImageType(type);
    document.getElementById(`${type}-upload`).click(); // Trigger the file input click
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setModalImage('');
  };

  if (error) {
    return <div className="text-red-500 text-center mt-4">{error}</div>;
  }

  if (!profile) {
    return <div className="text-center mt-4">Loading...</div>;
  }

  return (
    <div className="max-w-3xl mx-auto bg-white shadow-md rounded-md overflow-hidden mt-8">
      {/* Cover Photo */}
      <div
        className="relative h-60 bg-cover bg-center cursor-pointer"
        style={{ backgroundImage: `url(${profile.cover_pic || 'https://via.placeholder.com/800x500'})` }}
      >
        <label
          htmlFor="cover-upload"
          className="absolute top-4 right-4 cursor-pointer bg-white p-1 rounded-full shadow-lg hover:bg-gray-100"
        >
          <FaCamera className="text-gray-600 text-lg" />
        </label>
        <input
          id="cover-upload"
          type="file"
          accept="image/jpeg,image/jpg,image/png"
          onChange={(e) => handleFileChange(e, 'cover')}
          className="hidden"
        />
        <button
          onClick={() => openModal('cover')}
          className="absolute top-4 right-12 bg-blue-500 text-white px-2 py-1 rounded-full shadow-lg hover:bg-blue-600"
        >
          Upload
        </button>
      </div>

      {/* Profile Picture */}
      <div className="relative">
        <img
          src={profile.profile_pic || 'https://via.placeholder.com/100'}
          alt="Profile"
          className="w-24 h-24 rounded-full border-4 border-white shadow-md cursor-pointer object-cover"  
          onClick={() => openModal('profile')}
        />
        <label
          htmlFor="profile-upload"
          className="absolute bottom-2 left-12 cursor-pointer bg-white p-1 rounded-full shadow-lg hover:bg-gray-100"
        >
          <FaCamera className="text-gray-600 text-lg" />
        </label>
        <input
          id="profile-upload"
          type="file"
          accept="image/jpeg,image/jpg,image/png"
          onChange={(e) => handleFileChange(e, 'profile')}
          className="hidden"
        />
        <button
          onClick={() => openModal('profile')}
          className="absolute bottom-2 left-24 bg-blue-500 text-white px-2 py-1 rounded-full shadow-lg hover:bg-blue-600"
        >
          Upload
        </button>
      </div>

      {/* Profile Details */}
      <div className="p-6">
        <div className="flex justify-between items-center mb-4">
          <div>
            <h2 className="text-2xl font-semibold">{profile.first_name}</h2>
            <h2 className="text-sm text-teal-800 opacity-75 shadow-sm">{`@${profile.username}`}</h2>
          </div>
          <div className="flex space-x-4">
            <Link
              to="/profile-detail"
              className="text-blue-500 border border-blue-500 px-4 py-2 rounded-full hover:bg-blue-500 hover:text-white"
            >
              Profile
            </Link>
            <Link
              to="/edit-profile"
              className="text-blue-500 border border-blue-500 px-4 py-2 rounded-full hover:bg-blue-500 hover:text-white"
            >
              Edit Profile
            </Link>
          </div>
        </div>
        <p className="text-gray-600 mb-6">{profile.bio || 'No bio available'}</p>

        {/* New Post Box */}
        <div className="border rounded-lg p-4 mb-6">
          <textarea
            className="w-full p-2 border border-gray-300 rounded-md resize-none"
            rows="4"
            placeholder="What's on your mind?"
            value={newPostText}
            onChange={(e) => setNewPostText(e.target.value)}
          />
          <div className="flex items-center space-x-4 mt-4">
            <button onClick={() => setShowEmojiPicker(!showEmojiPicker)} className="text-gray-600">
              ðŸ˜€
            </button>
            {showEmojiPicker && (
              <EmojiPicker onEmojiClick={handleEmojiClick} />
            )}
            <button
              onClick={handlePostSubmit}
              className="bg-blue-500 text-white px-4 py-2 rounded-lg shadow-lg hover:bg-blue-600"
            >
              Post
            </button>
          </div>
        </div>

        {/* User Posts */}
        {posts.length > 0 ? (
          <div>
            {posts.map((post) => (
              <div key={post.id} className="border p-4 mb-4 rounded-lg">
                <p>{post.content}</p>
              </div>
            ))}
          </div>
        ) : (
          <p>No posts available.</p>
        )}
      </div>

      {/* Modal for Image Cropping */}
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="modal" overlayClassName="overlay">
        <div className="relative">
          <Cropper
            ref={cropperRef}
            src={modalImage}
            style={{ height: 400, width: '100%' }}
            aspectRatio={1}
            guides={false}
          />
          <button
            onClick={handleImageUpload}
            className="absolute bottom-4 right-4 bg-blue-500 text-white px-4 py-2 rounded-lg shadow-lg hover:bg-blue-600"
          >
            Save
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default ProfilePage;
