import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { BASE_URL } from '../config';

const SearchBar = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const user = useSelector((state) => state.user.user); // To use the logged-in user info

  useEffect(() => {
    if (query.length > 0) {
      searchUsers(query);
    } else {
      setResults([]);
    }
  }, [query]);

  const searchUsers = async (searchQuery) => {
    try {
      const response = await axios.post(
        `${BASE_URL}/api/interactions/search-users/`,
        { search_query: searchQuery },
        {
          headers: {
            Authorization: `Bearer ${token}`, // Pass the token in the Authorization header
          },
        }
      );
      setResults(response.data);
      setIsDropdownOpen(true); // Open the dropdown when results are available
    } catch (error) {
      console.error('Error fetching search results', error);
      setResults([]);
    }
  };
  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  const handleResultClick = () => {
    setIsDropdownOpen(false); // Close the dropdown after selection
  };

  return (
    <div className="relative">
      {/* Search Input */}
      <input
        type="text"
        className="border border-gray-300 rounded-full py-2 px-4 focus:outline-none w-64 md:w-96"
        placeholder="Search users..."
        value={query}
        onChange={handleInputChange}
        onFocus={() => setIsDropdownOpen(true)} // Open dropdown on focus
      />

      {/* Dropdown for Results */}
      {isDropdownOpen && results.length > 0 && (
        <ul className="absolute bg-white border border-gray-300 rounded-md mt-2 w-full max-h-64 overflow-y-auto z-50 shadow-lg">
          {results.map((result) => (
            <li key={result.id} className="hover:bg-gray-100 cursor-pointer">
              <Link
                to={`/profile/${result.username}`} 
                className="flex items-center py-2 px-4"
                onClick={handleResultClick}
              >
                <img
                  src={result.profile_pic || 'https://via.placeholder.com/30'} // Placeholder if no profile pic
                  alt={result.username}
                  className="w-8 h-8 rounded-full mr-2"
                />
                <span>{result.username}</span>
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchBar;
